// @flow

// Vendor
import React, { PropTypes } from 'react';
import { connect } from 'react-redux';
import { compose } from 'recompose';
import ShoppingCartIcon from 'react-icons/lib/fa/shopping-cart';
import Color from 'color';

// Custom
import { toggleInCart } from '@ncigdc/dux/cart';
import Button from '@ncigdc/uikit/Button';
import { withTheme } from '@ncigdc/theme';

/*----------------------------------------------------------------------------*/

const styles = {
  button: theme => ({
    padding: '3px 5px',
    margin: '0 auto',
    border: `1px solid ${theme.greyScale4}`,
  }),
  inactive: theme => ({
    backgroundColor: 'white',
    color: theme.greyScale2,
    ':hover': {
      backgroundColor: theme.greyScale6,
    },
  }),
  active: theme => ({
    backgroundColor: theme.success,
    color: 'white',
    ':hover': {
      backgroundColor: Color(theme.success).darken(0.3).rgbString(),
    },
  }),
};

const fileInCart = (files, file) => files.some(f => f.file_id === file.file_id);

const AddToCartButtonSingle = ({ dispatch, file, files, theme, ...props }) => (
  <Button
    style={{ ...styles.button(theme), ...(fileInCart(files, file) ? styles.active(theme) : styles.inactive(theme)) }}
    onClick={() => dispatch(toggleInCart(file))}
    {...props}
  >
    <ShoppingCartIcon />
  </Button>
);

AddToCartButtonSingle.propTypes = {
  files: PropTypes.array,
  file: PropTypes.object,
  dispatch: PropTypes.func,
};

/*----------------------------------------------------------------------------*/

export default
compose(
  connect(state => state.cart),
  withTheme
)(AddToCartButtonSingle);
