// @flow

import React from 'react';

import Column from '@ncigdc/uikit/Flex/Column';
import EntityPageVerticalTable from '@ncigdc/components/EntityPageVerticalTable';
import Card from '@ncigdc/uikit/Card';
import Link from '@ncigdc/components/Links/Link';

import { withTheme } from '@ncigdc/theme';

const Annotation = ({ node, theme }: { node: Object, theme: Object }) => (
  <Column spacing={theme.spacing}>
    <EntityPageVerticalTable
      id="summary"
      title={<span><i className="fa fa-table" /> Summary</span>}
      thToTd={[
        { th: 'Annotation UUID', td: node.annotation_id },
        { th: 'Entity UUID', td: <Link pathname={`/cases/${node.case_id}#biospecimen`}>{node.entity_id}</Link> },
        { th: 'Entity Barcode', td: node.entity_submitter_id },
        { th: 'Entity type', td: node.entity_type },
        { th: 'Case UUID', td: <Link pathname={`/cases/${node.case_id}`}>{node.case_id}</Link> },
        { th: 'Case Submitter ID', td: node.case_submitter_id },
        { th: 'Project ID', td: <Link pathname={`/projects/${node.project.project_id}`}>{node.project.project_id}</Link> },
        { th: 'Classification', td: node.classification },
        { th: 'Category', td: node.category },
        { th: 'Created On', td: node.created_datetime },
        { th: 'Status', td: node.status },
      ]}
      style={{ flex: 1 }}
    />
    <Card title="NOTES" >
      <div style={{ padding: 10 }}>
        {node.notes}
      </div>
    </Card>
  </Column>
);

export default withTheme(Annotation);
