/* @flow */

import React from 'react';

import Dropdown from '@ncigdc/uikit/Dropdown';
import Link from '@ncigdc/components/Links/Link';

export type TProps = {|
  prfSize: string,
  size: number,
  sizes?: Array<number>,
|};

const Sizes = ({ sizes = [20, 40, 60, 80, 100], size, prfSize }: TProps) => {
  return (
    <span>
      <Dropdown
        selected={size}
        dropdownStyle={{
          minWidth: '40px',
          width: '40px',
        }}
        itemStyle={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        {sizes.map(x => (
          size === x
            ? <span key={x}>{x}</span>
            : <Link key={x} merge query={{ [prfSize]: x }}>{x}</Link>
        ))}
      </Dropdown>
    </span>
  );
};

export default Sizes;
