/* @flow */

import React from 'react';

import Dropdown from '@ncigdc/uikit/Dropdown';
import DropdownItem from '@ncigdc/uikit/DropdownItem';
import Link from '@ncigdc/components/Links/Link';

export type TProps = {|
  prfSize: string,
  size: number,
  sizes?: Array<number>,
|};

const Sizes = ({ sizes = [20, 40, 60, 80, 100], size, prfSize }: TProps) => {
  return (
    <span>
      <Dropdown
        selected={size}
        dropdownStyle={{
          minWidth: '40px',
          width: '40px',
        }}
      >
        {sizes.map(x => (
          <DropdownItem
            key={x}
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            {x === size ? x : <Link merge query={{ [prfSize]: x }}>{x}</Link>}
          </DropdownItem>
        ))}
      </Dropdown>
    </span>
  );
};

export default Sizes;
