// @flow

import React from 'react';
import { Row, Column } from '@ncigdc/uikit/Flex';
import styled from '@ncigdc/theme/styled';
import CurrentFilters from './CurrentFilters';

const Container = styled(Row, {
  padding: '2rem 2.5rem 13rem',
});

const FacetsPanel = styled(Column, {
  width: ({ theme }) => theme.facetsPanelWidth,
});

const Content = styled(Column, {
  marginLeft: '18px',
  width: ({ theme }) => `calc(100% - ${theme.facetsPanelWidth} - 2.5rem)`,
});

type TProps = {|
  charts?: mixed,
  facets?: mixed,
  results?: mixed,
|};

const SearchPage = ({
  charts = <span />,
  facets = <span />,
  results = <span />,
}: TProps = {}) => (
  <Container>
    <FacetsPanel>{facets}</FacetsPanel>
    <Content>
      <CurrentFilters />
      {charts}
      {results}
    </Content>
  </Container>
);

export default SearchPage;
