/* @flow */

import Relay from 'react-relay';
import SearchTable from '@ncigdc/components/SearchTable';

export const AnnotationTableQuery = {
  fragments: {
    hits: () => Relay.QL`
      fragment on AnnotationConnection {
        total
        edges {
          node {
            id
            annotation_id
            case_id
            project {
              project_id
            }
            entity_type
            entity_id
            category
            classification
            created_datetime
          }
        }
      }
    `,
  },
};

const AnnotationTable = Relay.createContainer(
  SearchTable,
  AnnotationTableQuery
);

export default AnnotationTable;
