// @flow

import React from 'react';
import Relay from 'react-relay';
import withSize from '@ncigdc/utils/withSize';
import { isEqual, head } from 'lodash';
import { scaleOrdinal, schemeCategory10 } from 'd3';
import { compose, withState, withPropsOnChange } from 'recompose';
import withRouter from '@ncigdc/utils/withRouter';
import { makeFilter, addInFilters } from '@ncigdc/utils/filters';
import Showing from '@ncigdc/components/Pagination/Showing';
import { parseIntParam, parseFilterParam } from '@ncigdc/utils/uri';
import GeneLink from '@ncigdc/components/Links/GeneLink';
import EntityPageHorizontalTable from '@ncigdc/components/EntityPageHorizontalTable';
import Loader from '@ncigdc/uikit/Loaders/Loader';
import { Row } from '@ncigdc/uikit/Flex';
import { Tooltip } from '@ncigdc/uikit/Tooltip';
import Button from '@ncigdc/uikit/Button';
import { tableToolTipHint } from '@ncigdc/theme/mixins';
import { SpinnerIcon } from '@ncigdc/theme/icons';
import Hidden from '@ncigdc/components/Hidden';
import Pagination from '@ncigdc/components/Pagination';
import SurvivalIcon from '@ncigdc/theme/icons/SurvivalIcon';
import { getSurvivalCurves } from '@ncigdc/utils/survivalplot';
import ProjectBreakdown from '@ncigdc/containers/ProjectBreakdown';
import MutationsCount from '@ncigdc/containers/MutationsCount';
import CosmicIcon from '@ncigdc/theme/icons/Cosmic';
import CohortLink from '@ncigdc/components/Links/CohortLink';

const colors = scaleOrdinal(schemeCategory10);

const FrequentlyMutatedGenesTableComponent = compose(
  withRouter,
  withState('survivalLoadingId', 'setSurvivalLoadingId', ''),
  withPropsOnChange(
    (props, nextProps) => ['query', 'projectId', 'defaultFilters']
      .some((propName) => !isEqual(props[propName], nextProps[propName])),
    ({ relay, query, defaultFilters }) => {
      relay.setVariables({
        fetchData: true,
        fmgTable_offset: parseIntParam(query.fmgTable_offset, relay.route.params.fmgTable_offset || 0),
        fmgTable_size: parseIntParam(query.fmgTable_size, relay.route.params.fmgTable_size || 10),
        fmgTable_filters: parseFilterParam(query.fmgTable_filters, defaultFilters || null),
        geneCaseFilter: addInFilters(
          query.fmgTable_filters,
          makeFilter([
            {
              field: 'cases.available_variation_data',
              value: 'ssm',
            },
          ], false)
        ),
      });
    }
  ),
  withSize()
)(({
  projectIds = [],
  cohort: { genes, filteredCases, ssms },
  defaultFilters,
  relay,
  setSurvivalLoadingId,
  survivalLoadingId,
  setSelectedSurvivalData,
  selectedSurvivalData,
  survivalPlotSampleSize,
  projectBreakdown,
}) => {
  if (genes && !genes.hits.edges.length) {
    return <Row style={{ padding: '1rem' }}>No gene data found.</Row>;
  }

  const data = !genes ? [] : genes.hits.edges.map(x => x.node);

  const projectId = head(projectIds) || '';

  return (
    <Loader
      loading={!genes}
      height="387px"
    >
      <Row style={{ backgroundColor: 'white', padding: '1rem', justifyContent: 'space-between' }}>
        <Showing
          docType="genes"
          prefix={'fmgTable'}
          params={relay.route.params}
          total={!genes ? 0 : genes.hits.total}
        />
      </Row>
      <EntityPageHorizontalTable
        headings={[
          { key: 'symbol', title: 'Symbol' },
          { key: 'name', title: 'Name' },
          { key: 'cytoband', title: 'Cytoband' },
          ...(projectId ?
          [{
            key: 'num_affected_cases_project',
            title: (
              <Tooltip
                Component={
                  <span>
                    Breakdown of Affected Cases in {projectId} <br />
                    # of Cases where Gene is <br />mutated /# SSM tested Cases
                  </span>
                }
                style={tableToolTipHint()}
              >
                # Affected Cases<br />in {projectId}
              </Tooltip>),
          }] : []),
          ...(!projectId ?
          [{
            key: 'num_affected_cases_cohort',
            title: (
              <Tooltip
                Component={
                  <span>
                    Breakdown of Affected Cases in cohort <br />
                    # of Cases where Gene is <br />mutated /# SSM tested Cases
                  </span>
                }
                style={tableToolTipHint()}
              >
                # Affected Cases<br />in cohort
              </Tooltip>),
          }] : []),
          {
            key: 'projectBreakdown',
            title: (
              <Tooltip
                Component={
                  <span>
                    # of Cases where gene contains <br />
                    SSM filtered by current criteria <br />
                    / # SSM tested donors portal wide
                  </span>
                }
                style={tableToolTipHint()}
              >
                # Affected Cases<br /> Across all Projects
              </Tooltip>
            ),
          },
          {
            key: 'num_mutations',
            tdStyle: { textAlign: 'right' },
            title: (
              <Tooltip
                Component='# of SSM in the gene'
                style={tableToolTipHint()}
              >
                # Mutations
              </Tooltip>
            ),
          },
          {
            key: 'annotations',
            title: 'Annotations',
            tdStyle: { textAlign: 'center', padding: '5px 0 0 0' },
          },
          {
            title: <span>Survival <br />Analysis</span>,
            key: 'survival_plot',
            style: { textAlign: 'center', width: '100px' },
          },
        ]}
        data={data.map(({ score = 0, ...g }) => ({
          ...g,
          name: (
            <div style={{ maxWidth: '230px', whiteSpace: 'normal' }}>{g.name}</div>
          ),
          symbol: <GeneLink id={g.gene_id}>{g.symbol}</GeneLink>,
          cytoband: (g.cytoband || []).join(', '),
          num_affected_cases_project: (
            <span>
              <CohortLink
                query={{
                  searchTableTab: 'cases',
                  filters: makeFilter([
                    { field: 'genes.gene_id', value: [g.gene_id] },
                    { field: 'cases.available_variation_data', value: ['ssm'] },
                    { field: 'cases.project.project_id', value: [projectId] },
                  ], false),
                }}
              >
                {score.toLocaleString()}
              </CohortLink>
              <span> / </span>
              <CohortLink
                query={{
                  searchTableTab: 'cases',
                  filters: makeFilter([
                    { field: 'cases.available_variation_data', value: ['ssm'] },
                    { field: 'cases.project.project_id', value: [projectId] },
                  ], false),
                }}
              >
                {(filteredCases.hits.total || 0).toLocaleString()}
              </CohortLink>
              <span>&nbsp;({((score / filteredCases.hits.total) * 100).toFixed(2)}%)</span>
            </span>
          ),
          num_affected_cases_cohort: (
            <span>
              <CohortLink
                merge
                query={{
                  searchTableTab: 'cases',
                  filters: makeFilter([
                    { field: 'genes.gene_id', value: [g.gene_id] },
                    { field: 'cases.available_variation_data', value: ['ssm'] },
                  ], false),
                }}
              >
                {score.toLocaleString()}
              </CohortLink>
              <span> / </span>
              <CohortLink
                merge
                query={{
                  searchTableTab: 'cases',
                  filters: makeFilter([
                    { field: 'cases.available_variation_data', value: ['ssm'] },
                  ], false),
                }}
              >
                {(filteredCases.hits.total || 0).toLocaleString()}
              </CohortLink>
              <span>&nbsp;({((score / filteredCases.hits.total) * 100).toFixed(2)}%)</span>
            </span>
          ),
          projectBreakdown: (
            <ProjectBreakdown
              filters={makeFilter([{ field: 'genes.gene_id', value: g.gene_id }], false)}
              cohort={projectBreakdown}
              caseTotal={g.case.hits.total}
            />
          ),
          num_mutations: (
            <MutationsCount
              key={g.gene_id}
              ssms={ssms}
              filters={addInFilters(
                defaultFilters,
                makeFilter([
                  { field: 'genes.gene_id', value: [g.gene_id] },
                ], false)
              )}
            />
          ),
          annotations: g.is_cancer_gene_census &&
            <Tooltip Component='COSMIC'><CosmicIcon width={'20px'} height={'16px'} /></Tooltip>,
          survival_plot: (
            <Tooltip Component={`Click icon to plot ${g.symbol}`}>
              <Button
                style={{
                  padding: '2px 3px',
                  backgroundColor: colors(selectedSurvivalData.id === g.symbol ? 1 : 0),
                  color: 'white',
                  margin: '0 auto',
                }}
                onClick={() => {
                  if (g.symbol !== selectedSurvivalData.id) {
                    setSurvivalLoadingId(g.symbol);
                    getSurvivalCurves({
                      field: 'gene.symbol',
                      value: g.symbol,
                      currentFilters: projectId ?
                      {
                        op: 'and',
                        content: [
                          {
                            op: '=',
                            content: {
                              field: 'cases.project.project_id',
                              value: projectId,
                            },
                          },
                        ],
                      } : null,
                      size: survivalPlotSampleSize,
                    })
                      .then(data => {
                        setSelectedSurvivalData(data);
                        setSurvivalLoadingId('');
                      });
                  } else {
                    setSelectedSurvivalData({});
                  }
                }}
              >
                {survivalLoadingId === g.symbol ? <SpinnerIcon /> : <SurvivalIcon />}
                <Hidden>add to survival plot</Hidden>
              </Button>
            </Tooltip>
          ),
        }))
        .sort((a, b) => b.score - a.score)
        }
      />
      <Pagination
        prefix={'fmgTable'}
        params={relay.route.params}
        total={!genes ? 0 : genes.hits.total}
      />
    </Loader>
  );
});

export const FrequentlyMutatedGenesTableQuery = {
  initialVariables: {
    fetchData: false,
    fmgTable_filters: null,
    fmgTable_size: 10,
    fmgTable_offset: 0,
    score: 'case.project.project_id',
    geneCaseFilter: null,
  },
  fragments: {
    cohort: () => Relay.QL`
      fragment on Cohort {
        ssms {
          ${MutationsCount.getFragment('ssms')}
        }
        x: cases { hits(first: 0) { total }}
        filteredCases: cases @include(if: $fetchData) {
          hits(first: 0 filters: $geneCaseFilter) {
            total
          }
        }
        genes @include(if: $fetchData) {
          hits (
            first: $fmgTable_size
            offset: $fmgTable_offset
            filters: $fmgTable_filters
            score: $score
          ) {
            total
            edges {
              node {
                score
                symbol
                name
                cytoband
                gene_id
                is_cancer_gene_census
                case {
                  hits(first: 0 filters: $geneCaseFilter) {
                    total
                  }
                }
              }
            }
          }
        }
      }
    `,
  },
};

const FrequentlyMutatedGenesTable = Relay.createContainer(
  FrequentlyMutatedGenesTableComponent,
  FrequentlyMutatedGenesTableQuery
);

export default FrequentlyMutatedGenesTable;
