/* @flow */

import React from 'react';
import Relay from 'react-relay';

import Home from '@ncigdc/components/Home';
import type { TBucket } from '@ncigdc/components/Aggregations/types';

export type TProps = {
  viewer: {
    repository: {
      cases: {
        hits: {
          pagination: {
            total: number,
          },
        },
      },
      files: {
        hits: {
          pagination: {
            total: number,
          },
        },
      },
    },
    projects: {
      aggregations: {
        primary_site: { buckets: [TBucket] },
      },
      hits: {
        edges: Array<{|
          node: {|
           id: string,
           project_id: string,
           primary_site: string,
           summary: {
             case_count: number,
             file_count: number,
           },
         |},
       |}>,
       pagination: {
         total: number,
       },
      },
    },
  },
};

type THomePageComponent = (props: TProps) => React.Element<*>;
export const HomePageComponent: THomePageComponent = ({
  viewer = {
    repository: {
      cases: {
        hits: {
          pagination: {
            total: 0,
          },
        },
      },
      files: {
        hits: {
          pagination: {
            total: 0,
          },
        },
      },
    },
    projects: {
      aggregations: {
        primary_site: { buckets: [] },
      },
      hits: {
        edges: [],
        pagination: {
          total: 0,
        },
      },
    },
  },
} = {}) => (
  <Home
    humanBodyData={viewer.projects.hits.edges.map(x => x.node)}
    projectsCountData={viewer.projects.hits}
    primarySitesCountData={viewer.projects.aggregations}
    casesCountData={viewer.repository.cases.hits}
    filesCountData={viewer.repository.files.hits}
  />
);

export const HomePageQuery = {
  initialVariables: {
    sort: 'summary.case_count:desc',
  },
  fragments: {
    viewer: () => Relay.QL`
      fragment on Root {
        projects {
          aggregations {
            primary_site {
              buckets {
                key
              }
            }
          }
          hits(first: 1000 sort: $sort) {
            edges {
              node {
                id
                project_id
                primary_site
                summary {
                  case_count
                  file_count
                }
              }
            }
            pagination {
              total
            }
          }
        }
        repository {
          cases {
            hits(first: 0 offset: 0) {
              pagination {
                total
              }
            }
          }
          files {
            hits(first: 0 offset: 0) {
              pagination {
                total
              }
            }
          }
        }
      }
    `,
  },
};

const HomePage = Relay.createContainer(
  HomePageComponent,
  HomePageQuery
);

export default HomePage;
