// @flow

import React from 'react';
import Relay from 'react-relay';
import { compose, withState } from 'recompose';
import { makeFilter } from '@ncigdc/utils/filters';
import TogglableUl from '@ncigdc/uikit/TogglableUl';

const ProjectBreakdownComponent = compose(
  withState('loading', 'setLoading', false)
)(({
  loading,
  setLoading,
  cohort: { cases },
  numCasesAggByProject,
  caseTotal,
  relay,
  filters,
}) => {
  const filteredAggs = !cases.aggregations ? {} : cases.aggregations.project__project_id.buckets.reduce((acc, b) => ({
    ...acc,
    [b.key]: b.doc_count,
  }), {});

  return (
    <TogglableUl
      onToggle={() => {
        relay.setVariables({
          fetchData: true,
          filters,
        });
        setLoading(l => !l);
      }}
      items={[
        <span key="total">
          {caseTotal.toLocaleString()} / {cases.hits.total.toLocaleString()}
          &nbsp;({((caseTotal / cases.hits.total) * 100).toFixed(2)}%)
        </span>,
        ...(loading && !cases.aggregations && [<i className="fa fa-spinner fa-spin" />]),
        ...Object.entries(filteredAggs)
          .sort(([ak, av], [bk, bv]) => (bv / numCasesAggByProject[bk]) - (av / numCasesAggByProject[ak]))
          .map(([k, v]) => (
            <span key={k}>
              {k}: {v} / {numCasesAggByProject[k]}
              &nbsp;({((v / numCasesAggByProject[k]) * 100).toFixed(2)}%)
            </span>
          )),
      ]}
    />
  );
});

export const ProjectBreakdownQuery = {
  initialVariables: {
    fetchData: false,
    filters: null,
    ssmTested: makeFilter([{
      field: 'available_variation_data',
      value: 'ssm',
    }], false),
  },
  fragments: {
    cohort: () => Relay.QL`
      fragment on Cohort {
        cases {
          hits(first: 1 filters: $ssmTested) { total }
          aggregations(filters: $filters) @include(if: $fetchData) {
            project__project_id {
              buckets {
                doc_count
                key
              }
            }
          }
        }
      }
    `,
  },
};

const ProjectBreakdown = Relay.createContainer(
  ProjectBreakdownComponent,
  ProjectBreakdownQuery
);

export default ProjectBreakdown;
