/* @flow */

import React from 'react';
import { createFragmentContainer, graphql } from 'react-relay/compat';
import SearchTable from '@ncigdc/components/SearchTable';

const ProjectTable = createFragmentContainer(
  props => <SearchTable entityType='projects' {...props} />,
  graphql`
    fragment ProjectTable_hits on ProjectConnection {
      total
      edges @relay(plural: true) {
        node {
          id
          project_id
          disease_type
          program {
            name
          }
          primary_site
          summary {
            case_count
            data_categories {
              case_count
              data_category
            }
            file_count
          }
        }
      }
    }
  `
);

export default ProjectTable;
