// @flow

const UPDATE_CACHED_COMPONENT = 'UPDATE_CACHED_COMPONENT';

const updateCachedComponent = (payload: Object) => ({
  type: UPDATE_CACHED_COMPONENT,
  payload,
});

const initialState = {
  component: null,
  path: '',
};

const reducer = (state = initialState, action: Object) => {
  switch (action.type) {
    case 'UPDATE_CACHED_COMPONENT':
      return action.payload;
    default:
      return state;
  }
};

/*----------------------------------------------------------------------------*/

export default reducer;
export { updateCachedComponent };
