// @flow

const initialState = {
  isBannerDismissed: false,
}

export const dismissBanner = () => ({
  type: 'DISMISS_BANNER',
});

function modal(state = initialState, action) {
  switch (action.type) {
    case 'DISMISS_BANNER':
      return {
        isBannerDismissed: true,
      };
    default:
      return state;
  }
}

/*----------------------------------------------------------------------------*/

export default modal;
