// @flow
import React from 'react';
import { Tooltip } from '@ncigdc/uikit/Tooltip';

const impactBubble = {
  color: 'white',
  padding: '2px 5px',
  borderRadius: '8px',
  fontSize: '10px',
  fontWeight: 'bold',
  display: 'inline-block',
};

export const impactColors = {
  HIGH: 'rgb(185, 36, 36)',
  MODERATE: '#634d0c',
  LOW: '#015c0a',
  MODIFIER: '#c94d18',
};

const styles = {
  impact: {
    HIGH: {
      ...impactBubble,
      backgroundColor: impactColors.HIGH,
    },
    MODERATE: {
      ...impactBubble,
      backgroundColor: impactColors.MODERATE,
    },
    LOW: {
      ...impactBubble,
      backgroundColor: impactColors.LOW,
    },
    MODIFIER: {
      ...impactBubble,
      backgroundColor: impactColors.MODIFIER,
    },
  },
};

type TProps = {
  impact: 'HIGH' | 'MODERATE' | 'LOW' | 'MODIFIER',
};

export default ({ impact }: TProps) => (
  <Tooltip Component={impact}>
    <span
      style={styles.impact[impact]}
    >
      {impact.slice(0, (impact === 'MODIFIER' ? 2 : 1))}
    </span>
  </Tooltip>
);
