// @flow
import React from 'react';
import Dropdown from '@ncigdc/uikit/Dropdown';
import UnstyledButton from './UnstyledButton';

export default ({
  first,
  sizes = [20, 40, 60, 80, 100],
  onChange,
}: {
  first: number,
  sizes?: Array<number>,
  onChange: Function,
}) => (
  <Dropdown
    selected={first}
    dropdownStyle={{
      minWidth: '40px',
      width: '40px',
      zIndex: 101,
    }}
    itemStyle={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    }}
  >
    {sizes.map(x => (
      first === x
        ? <span key={x}>{x}</span>
        : <UnstyledButton key={x} onClick={() => onChange(x)}>{x}</UnstyledButton>
    ))}
  </Dropdown>
);
