// @flow

// Vendor
import React, { PropTypes } from 'react';
import { connect } from 'react-redux';
import { setTooltip } from './dux';

const Tooltip = ({ Component, children, dispatch, ...props }) => (
  <span
    onMouseOver={() => dispatch(setTooltip(Component))}
    onMouseOut={() => dispatch(setTooltip(null))}
    {...props}
  >
    {children}
  </span>
);

Tooltip.propTypes = {
  Component: PropTypes.node,
  children: PropTypes.node,
  dispatch: PropTypes.func,
};

export default connect()(Tooltip);
