// @flow

import { CALL_API } from 'redux-api-middleware';
import { join as joinPath } from 'path';

declare var API: string;
declare var GDC_AUTH: string;

const DEFAULTS = {
  method: 'get',
  credentials: 'same-origin',
  headers: {
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': true,
    'X-Auth-Token': 'secret admin token',
  },
};

export function fetchAuth(options) {
  return {
    [CALL_API]: {
      ...DEFAULTS,
      ...options,
      endpoint: joinPath(GDC_AUTH, options.endpoint),
    },
  };
}

// $FlowIgnore
export const fetchApi = (endpoint, opts = {}) => {
  if (opts.body) {
    opts.body = opts.body ? JSON.stringify(opts.body) : '';
    opts.method = 'POST';
  }
  return fetch(joinPath(API, endpoint), opts).then(r => r.json());
};
