/* @flow */
import _ from 'lodash';

export default (facet) => {
  if (_.includes(facet.field, 'datetime')) {
    return 'datetime';
  } else if (_.some(['_id', '_uuid', 'md5sum', 'file_name'], idSuffix => _.includes(facet.field, idSuffix))) {
    return 'id';
  } else if (facet.type === 'long') {
    return 'range';
  } else {
    return 'terms';
  }
};
