// @flow
/* eslint fp/no-rest-parameters:0 */

import { compose, withProps, mapProps } from 'recompose';

const namespace = (ns, ...hocs) => compose(
  withProps(props => ({ [Symbol.for('parentProps')]: props })),
  ...hocs,
  mapProps(props => ({ [ns]: props, ...props[Symbol.for('parentProps')] }))
);

export default namespace;
